INSERT INTO films (id, title, about, description, director, rating, image, cover, tags) VALUES
('0e33c7f6-27a7-4aa0-8e61-65d7e5effecf', 'Архитекторы общества', 'Документальный фильм, исследующий влияние искусственного интеллекта на общество и этические, философские и социальные последствия технологии.', 'Документальный фильм Итана Райта исследует влияние технологий на современное общество, уделяя особое внимание роли искусственного интеллекта в формировании нашего будущего. Фильм исследует этические, философские и социальные последствия гонки технологий ИИ и поднимает вопрос: какой мир мы создаём для будущих поколений.', 'Итан Райт', 2.9, '/bg1s.jpg', '/bg1c.jpg', 'Документальный'),

('51b4bc85-646d-47fc-b988-3e7051a9fe9e', 'Недостижимая утопия', 'Провокационный фильм-антиутопия, исследующий темы свободы, контроля и цены совершенства.', 'Провокационный фильм-антиутопия режиссера Харрисона Рида. Действие фильма разворачивается в, казалось бы, идеальном обществе, и рассказывает о группе граждан, которые начинают подвергать сомнению систему. Фильм исследует темы свободы, контроля и цены совершенства.', 'Харрисон Рид', 9.0, '/bg3s.jpg', '/bg3c.jpg', 'Рекомендуемые'),

('3bedbc5a-844b-40eb-9d77-83b104e0cf75', 'Звёздное путешествие', 'Научно-фантастический фильм о команде астронавтов, исследующий темы жизнестойкости, надежды и силы человеческих связей', '«Звёздное путешествие» — прекрасный научно-фантастический фильм о команде астронавтов, путешествующих по галактике в поисках нового дома для человечества. Помимо потрясающей работы оператора и специалистов по визуальным эффектам, можно отметить темы, исследуемые в фильме: жизнестойкости, надежды и силы человеческих связей.', 'Элиза Уиттакер', 8.5, '/bg5s.jpg', '/bg5c.jpg', 'Рекомендуемые'),

('5b70cb1a-61c9-47b1-b207-31f9e89087ff', 'Стражи Гримуара', 'Фэнтезийное приключение об истинном значении дружбы, мужества и силы знаний', 'Захватывающее фэнтезийное приключение, которое рассказывает о группе героев, которые должны защитить древний магический том от попадания в руки тёмного колдуна. История об истинном значении дружбы, мужества и силы знаний.', 'Лила Васкес', 8.9, '/bg2s.jpg', '/bg2c.jpg', 'Рекомендуемые'),

('0354a762-8928-427f-81d7-1656f717f39c', 'Парадокс Нексуса', 'Фильм об эксперименте по соединению человеческих умов. Исследует вопросы неприкосновенности частной жизни, идентичности и самой природы человеческого сознания', 'В фильме исследуются последствия новаторского эксперимента по соединению человеческих умов. По мере развития проекта участники сталкиваются с вопросами неприкосновенности частной жизни, идентичности и самой природы человеческого сознания.', 'Оливер Беннет', 9.5, '/bg4s.jpg', '/bg4c.jpg', 'Рекомендуемые'),

('92b8a2a7-ab6b-4fa9-915b-d27945865e39', 'Сон в летний день', 'Фэнтези-фильм о группе друзей попавших в волшебный лес, где время остановилось.', 'Причудливый фэнтези-фильм, действие которого происходит в волшебном лесу, где время остановилось. Группа друзей натыкается на это заколдованное царство и поначалу проникается беззаботным духом обитателей, но потом друзьям приходится разойтись. А как встретиться снова, если нет ни времени, ни места встречи?', 'Амелия Хьюз', 8.1, '/bg6s.jpg', '/bg6c.jpg', 'Рекомендуемые');

